   case QColor::Rgb:
   {
       qreal r, g, b, a;
       %CPPSELF.getRgbF(&r, &g, &b, &a);
       QString repr = QString::asprintf("PySide2.QtGui.QColor.fromRgbF(%.6f, %.6f, %.6f, %.6f)", r, g, b, a);
       %PYARG_0 = Shiboken::String::fromCString(qPrintable(repr));
