   }
   case QColor::Hsv:
   {
       qreal h, s, v, a;
       %CPPSELF.getHsvF(&h, &s, &v, &a);
       QString repr = QString::asprintf("PySide2.QtGui.QColor.fromHsvF(%.6f, %.6f, %.6f, %.6f)", h, s, v, a);
       %PYARG_0 = Shiboken::String::fromCString(qPrintable(repr));
